###########################################################################/**# @RdocFunction toEPS# @alias toPDF# @alias toPNG# @alias toSVG## @title "Methods for creating image files of a specific format"## \description{#  @get "title".# }# # @synopsis## \arguments{#   \item{name}{A @character string specifying the name of the image file.}#   \item{...}{Additional arguments passed to @see "devEval", e.g.#      \code{tags} and \code{aspectRatio}.}#   \item{field}{Passed to @see "devEval".}# }## \value{#   Returns by default the so called \emph{fullname} of the generated#   image file.  The fullname of an file is the filename without the#   extension.# }## @author## \seealso{#   These functions are wrappers for @see "devEval".#   See @see "devOptions" to change the default dimensions for#   a specific device type.# }## @keyword device# @keyword utilities#*/########################################################################### toEPS <- function(name, ..., field="fullname") {  devEval(type="eps", name=name, ..., field=field);}toPDF <- function(name, ..., field="fullname") {  devEval(type="pdf", name=name, ..., field=field);}toPNG <- function(name, ..., field="fullname") {  devEval(type="png", name=name, ..., field=field);}toSVG <- function(name, ..., field="fullname") {  devEval(type="svg", name=name, ..., field=field);}############################################################################### HISTORY:# 2011-11-05# o Added toSVG().# o Moved from private R.rsp.addons package to R.utils.# o Dropped default 'width' from toPNG(), toPDF() and toEPS().  No longer#   required from R.utils v1.9.1.# 2011-10-31# o CLEANUP: toPNG() etc now utilizes new 'field' argument of devEval().# 2011-09-12# o Added toEPS().# 2011-06-15# o Added argument 'field' to toPNG() and toPDF().# 2011-05-23# o Added toPNG() and toPDF().##############################################################################